    import random
    from typing import List

    class Question:
        def __init__(self, prompt: str, options: List[str]):
            self.prompt = prompt
            self.options = options

        def ask(self) -> str:
            print(self.prompt)
            return input("Enter your answer: ")

        def check(self, answer: str) -> bool:
            return answer in self.options

    def main():
        questions = [
            Question("What is the capital of France?", ["Paris", "Lyon", "Marseille"]),
            Question(
                "What is 2 + 2?",
                ["4", "5", "6"],
            ),
            Question("", ["Yes", "No"]),
        ]

        score = 0
        for question in questions:
            answer = question.ask()
            if question.check(answer):
                score += 1
                print(f"Correct! Your score is {score}/{len(questions)}")
            else:
                print("Incorrect. Try again.")

    if __name__ == "__main__":
        main()
